mapper.select:
SELECT whitelist.id as id, whitelist.real_name AS realName, whitelist.white_status AS whiteStatus, whitelist.card AS card, whitelist.telphone AS telPhone, whitelist.job AS job, whitelist.latest_pay AS latestPay, whitelist.local_monthly_min_wage AS localMonthlyMinWage, contractor.contractor_name as contractorName
FROM byx_white_list whitelist LEFT JOIN byx_contractor contractor ON whitelist.contractor_id = contractor.id
and contractor.user_id like '%${param.userId}%'
and whitelist.card = #{param.card,jdbcType=VARCHAR}
and whitelist.real_name like '%${param.realName}%'
and whitelist.telphone like '%${param.mobile}%'
and whitelist.contractor_id = #{param.contractorId,jdbcType=VARCHAR}
/*order by CREAT_TIME desc*/

SELECT byxWhite.id as id
FROM byx_white_list byxWhite
WHERE byxWhite.card=#{card}
